import React, { Component } from "react";

class Title extends Component {
  state = {
    dataType: ""
  };

  radioHandler = event => {
    console.log("event:", event.target.value);
    this.setState({ dataType: event.target.value });
  };
  render() {
    return (
      <div>
        <div id="nav">
          <h2>
            CO<sub>2</sub> Dashboard
          </h2>
          <p>
            Explore CO<sub>2</sub> emissions by year. Current year:{" "}
            <span id="year-val"></span>
            <input id="year" type="range" step="1" />
          </p>
          <p>
            Choose between total emissions or emissions per capita.
            <div onChange={this.radioHandler}>
              <input type="radio" name="data-type" value="emissions" checked />
              <label>Emissions</label>
              <input type="radio" name="data-type" value="emissionsPerCapita" />
              <label>Emissions Per Capita</label>
            </div>
          </p>
          <p>Click on a country to see its trends by year.</p>
        </div>
      </div>
    );
  }
}

export default Title;
