import React, { Component } from "react";
import allData from "../data/all_data.csv";
// import D3Chart from "./D3Chart";
import * as d3 from "d3";

class MapChartWrapper extends Component {
  constructor() {
    super();
    var promises = [
      d3.json("http://unpkg.com/world-atlas@1.1.4/world/50m.json"),
      d3.csv(allData, row => {
        return {
          continent: row.Continent,
          country: row.Country,
          countryCode: row["Country Code"],
          emissions: +row["Emissions"],
          emissionsPerCapita: +row["Emissions Per Capita"],
          region: row.Region,
          year: +row.Year
        };
      })
    ];
    Promise.all(promises)
      .then(data => {
        var mapData = data[0];
        var newData = data[1];
        console.log("hey data:", newData);
        ready(data[0], data[1]);
      })
      .catch(error => {
        console.log("error", error);
      });

    function ready(mapData, data) {
      console.log("mapData:", mapData);
      console.log("data:", data);
    }
  }

  componentDidMount() {
    // new D3Chart(this.refs.mapChart);
  }

  render() {
    return <div ref="mapChart"></div>;
  }
}

export default MapChartWrapper;
