import React from "react";
import { Card, CardGroup } from "react-bootstrap";
import increase from "../assets/up-arrow.png";
import decrease from "../assets/down-arrow.png";

class HeaderCardGroup extends React.Component {
  state = {
    climateData: [
      {
        title: "carbon dioxide",
        increase: true,
        data: "413",
        unit: "parts per million"
      },
      {
        title: "global temperature",
        increase: true,
        data: "1.9",
        unit: "ºF since 1880"
      },
      {
        title: "arctic ice minimum",
        increase: false,
        data: "12.85",
        unit: "percent per decade"
      },
      {
        title: "ice sheets",
        increase: false,
        data: "428",
        unit: "Gigatonnes per year"
      }
    ]
  };

  render() {
    const { climateData } = this.state;

    return (
      <div>
        <CardGroup>
          {climateData.map((data, i) => {
            return (
              <Card key={i}>
                <Card.Body>
                  <Card.Title>{data.title}</Card.Title>

                  <Card.Text style={{ display: "inline-block" }}>
                    {data.increase ? (
                      <img src={increase}></img>
                    ) : (
                      <img src={decrease}></img>
                    )}
                  </Card.Text>
                  <Card.Text style={{ display: "inline-block" }}>
                    {data.data}
                  </Card.Text>
                  <Card.Text style={{ display: "inline-block" }}>
                    {data.unit}
                  </Card.Text>
                </Card.Body>
              </Card>
            );
          })}

          {/* <Card>
            <Card.Body>
              <Card.Title>Card title</Card.Title>
              <Card.Text>
                This is a wider card with supporting text below as a natural
                lead-in to additional content. This content is a little bit
                longer.
              </Card.Text>
            </Card.Body>
          </Card> */}
        </CardGroup>
      </div>
    );
  }
}

export default HeaderCardGroup;
