import React, { Component } from "react";
import allData from "../data/all_data.csv";
// import D3Chart from "./D3Chart";
import * as d3 from "d3";

class MapChartWrapper extends Component {
  constructor() {
    super();
    this.state = {
      extremeYears: "",
      currentYear: "",
      currentDataType: "",
      geoData: "",
      width: "",
      height: ""
    };

    var promises = [
      d3.json("http://unpkg.com/world-atlas@1.1.4/world/50m.json"),
      d3.csv(allData, row => {
        return {
          continent: row.Continent,
          country: row.Country,
          countryCode: row["Country Code"],
          emissions: +row["Emissions"],
          emissionsPerCapita: +row["Emissions Per Capita"],
          region: row.Region,
          year: +row.Year
        };
      })
    ];
    Promise.all(promises)
      .then(data => {
        ready(data[0], data[1]);
      })
      .catch(error => {
        console.log("error", error);
      });

    function ready(mapData, data) {
      var extremeYears = d3.extent(data, d => d.year);
      var currentYear = extremeYears[0];
      var currentDataType = d3
        .select('input[name="data-type"]:checked')
        .attr("value");
      var geoData = topojson.feature(mapData, mapData.objects.countries)
        .features;

      var width = +d3.select(".chart-container").node().offsetWidth;
      var height = 300;

      this.setState({
        extremeYears,
        currentYear,
        currentDataType,
        geoData,
        width,
        height
      });
    }
  }

  componentDidMount() {
    // new D3Chart(this.refs.mapChart);
  }

  render() {
    return <div ref="mapChart"></div>;
  }
}

export default MapChartWrapper;
