import React, { Component } from "react";
import allData from "../data/all_data.csv";
import MapChart from "./MapChart";
import * as d3 from "d3";
import * as topojson from "topojson";

class MapChartWrapper extends Component {
  constructor() {
    super();
    this.state = {
      extremeYears: "",
      currentYear: "",
      //   currentDataType: "",
      geoData: "",
      width: "",
      height: ""
    };

    var promises = [
      d3.json("http://unpkg.com/world-atlas@1.1.4/world/50m.json"),
      d3.csv(allData, row => {
        return {
          continent: row.Continent,
          country: row.Country,
          countryCode: row["Country Code"],
          emissions: +row["Emissions"],
          emissionsPerCapita: +row["Emissions Per Capita"],
          region: row.Region,
          year: +row.Year
        };
      })
    ];
    Promise.all(promises)
      .then(data => {
        this.ready.bind(this)(data[0], data[1]);
      })
      .catch(error => {
        console.log("error", error);
      });
  }

  ready(mapData, data) {
    var extremeYears = d3.extent(data, d => d.year);
    var currentYear = extremeYears[0];
    // var currentDataType = d3
    //   .select('input[name="data-type"]:checked')
    //   .attr("value");
    console.log("currentdata:", currentYear);
    var geoData = topojson.feature(mapData, mapData.objects.countries).features;
    console.log("geoData:", geoData);

    var width = +d3.select(".chart-container").node().offsetWidth;

    var height = 300;

    this.setState({
      extremeYears,
      currentYear,
      //   currentDataType,
      geoData,
      width,
      height
    });
  }

  componentDidMount() {
    // new D3Chart(this.refs.mapChart);

    this.setState({
      mapchart: new MapChart(
        this.refs.mapChart,
        this.state.width,
        (this.state.width * 4) / 5
      )
    });
  }

  render() {
    const { width, height } = this.state;

    return (
      <div
        ref="mapChart"
        className="chart-container"
        style={{ width: width, height: height }}
      ></div>
    );
  }
}

export default MapChartWrapper;
