import * as d3 from "d3";

var width;
var height;
export default class MapChart {
  constructor(element) {
    // console.log("element", element);
    // console.log("check element:", element.offsetWidth);
    width = element.offsetWidth;
    height = 300;
    d3.select(element)
      .attr("width", width)
      .attr("height", height)
      .append("text")
      .attr("x", width / 2)
      .attr("y", "1em")
      .attr("font-size", "1.5em")
      .style("text-anchor", "middle")
      .classed("map-title", true);
  }

  drawMap(geoData, climateData, year, dataType) {
    console.log(geoData);
    console.log(climateData);
    console.log(year);
    console.log(dataType);
  }
}
